if (cat == 'Maternity' || cat == 'Medical (Self)') {
 if (ed == 'Faculty' || ed == 'Academic' || ed == 'Faculty (Non-HCOMP)') {
 //NOVA Leaves Maternity/Employee Own Illness Leave Designation Faculty/Academic Task
 templateID = 'f647949b8762c2500548ca68cebb3523';
