 }
}
else if (cat == 'Parental Bonding' || cat == 'Medical (Family Member)') {
 if (ed == 'Faculty' || ed == 'Academic' || ed == 'Faculty (Non-HCOMP)') {
 //NOVA Leaves Parental Bonding/Care for Family Member Leave Designation Faculty/Academic Task
 templateID = 'fcc7105f8762c2500548ca68cebb35a0';
 }
 else {
 //NOVA Leaves Parental Bonding/Care for Family Member Leave Designation Postdoc Task
 templateID = 'afa7d89b8762c2500548ca68cebb3503';
 }
}
else if (ed != 'Faculty' && ed != 'Academic' && ed != 'Faculty (Non-HCOMP)' && cat == 'Personal') {
 //NOVA Leaves Postdoc Personal Leave Designation Task
 templateID = 'f3e9509f8762c2500548ca68cebb3573';
}
else {
 //NOVA Leaves Faculty/Academic Personal/All Academic/Jury Duty/Repro Loss/Bereavement Leave Designation Task
